import React from 'react';
import { PlusCircle, BookOpen } from 'lucide-react';
import { Button } from "@/components/ui/button";

const Sidebar = ({ isOpen, courses, onCreateCourse, onCourseClick }) => {
  return (
    <div className={`sidebar ${isOpen ? 'open' : ''} w-64 bg-gray-100 h-screen fixed left-0 top-16 p-4 transition-transform duration-300 ease-in-out ${isOpen ? 'translate-x-0' : '-translate-x-full'}`}>
      <div className="flex flex-col gap-4">
        <Button 
          onClick={onCreateCourse}
          className="w-full flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white"
        >
          <PlusCircle className="h-5 w-5" />
          Crear Curso
        </Button>

        <div className="space-y-2">
          {courses.map((course) => (
            <Button
              key={course.id}
              variant="ghost"
              className="w-full flex items-center gap-2 justify-start"
              onClick={() => onCourseClick(course.id, course.name)}
              style={{ borderLeft: `4px solid ${course.color}` }}
            >
              <BookOpen className="h-4 w-4" />
              {course.name}
            </Button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
