import React from 'react';

function CourseCard({ course, onDeleteCourse }) {
  return (
    <div className="course-card" style={{ backgroundColor: course.color }}>
      <div className="course-header">
        <h2>{course.name}</h2>
      </div>
      <div className="course-footer">
        <button className="footer-action-button" onClick={() => console.log(`Editar curso: ${course.id}`)}>
          <span className="material-icons">edit</span>
        </button>
        <button className="footer-action-button" onClick={() => onDeleteCourse(course.id)}>
          <span className="material-icons">delete</span>
        </button>
      </div>
    </div>
  );
}

export default CourseCard;
